let geoPskov = Geocoding(id: 504341, name: "Pskov", latitude: 57.8136, longitude: 28.3496, country: "Russia")


extension GeoWeather {

    static let sampleData = [
        GeoWeather(id: 0, geocoding: geoMoscow, weather: realWeatherMoscow),
        GeoWeather(id: 1, geocoding: geoPskov, weather: realWeatherPskov)
    ]
}

let realWeatherPskov: Weather = {
    return try! JSONDecoder().decode(WeatherJSON.self, from: weatherJSONDataPskov).weather
}()

let realWeatherMoscow: Weather = {
    return try! JSONDecoder().decode(WeatherJSON.self, from: weatherJSONDataMoscow).weather
}()

